import { Client, Account, ID } from "appwrite";
import { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");

  // Set up the Appwrite client
  const client = new Client();
  const account = new Account(client);

  client
    .setEndpoint("https://cloud.appwrite.io/v1") // Appwrite endpoint
    .setProject("670f76cb0008eca9526a"); // Replace with your Appwrite project ID

  // Login Handler
  const handleLogin = async () => {
    try {
      await account.createEmailSession(email, password);
      alert("Logged in successfully!");
    } catch (error) {
      alert("Login failed: " + error.message);
    }
  };

  // Signup Handler with Name and Phone Number
  const handleSignup = async () => {
    try {
      // Create user with email and password
      await account.create(ID.unique(), email, password, name);

      // Send verification email
      await account.createVerification(email);

      alert("Account created! Please check your email for verification.");

      // You can store additional user data such as phone number in Appwrite's database collection
      // If you need phone number storage, consider using Appwrite's Database to store it separately
    } catch (error) {
      alert("Signup failed: " + error.message);
    }
  };

  // Email Verification
  const handleResendVerification = async () => {
    try {
      await account.createVerification(email);
      alert("Verification email sent again! Please check your inbox.");
    } catch (error) {
      alert("Verification failed: " + error.message);
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100">
      <div className="w-full max-w-md p-6 bg-white rounded shadow-md">
        <h2 className="text-center text-2xl font-bold mb-6">
          {isLogin ? "Login" : "Sign Up"}
        </h2>

        {/* Name Field for Signup */}
        {!isLogin && (
          <input
            type="text"
            placeholder="Full Name"
            className="w-full p-2 mb-4 border rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        )}

        <input
          type="email"
          placeholder="Email"
          className="w-full p-2 mb-4 border rounded"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full p-2 mb-4 border rounded"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {/* Phone Number Field for Signup */}
        {!isLogin && (
          <input
            type="text"
            placeholder="Phone Number (optional)"
            className="w-full p-2 mb-4 border rounded"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
        )}

        <button
          className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          onClick={isLogin ? handleLogin : handleSignup}
        >
          {isLogin ? "Login" : "Sign Up"}
        </button>

        <p className="text-center mt-4">
          {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
          <span
            className="text-blue-500 cursor-pointer"
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin ? "Sign Up" : "Login"}
          </span>
        </p>

        {/* Resend Verification for Signup */}
        {!isLogin && (
          <p className="text-center mt-2 text-sm text-gray-500">
            Didn’t receive a verification email?{" "}
            <span
              className="text-blue-500 cursor-pointer"
              onClick={handleResendVerification}
            >
              Resend Email
            </span>
          </p>
        )}
      </div>
    </div>
  );
};

export default AuthPage;
