import { Client, Account, Storage } from "appwrite";
import { useState } from "react";

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const client = new Client();
  const account = new Account(client);
  const storage = new Storage(client);

  client
    .setEndpoint("https://cloud.appwrite.io/v1")
    .setProject("670f76cb0008eca9526a");

  // Handle file upload to Appwrite storage
  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    setLoading(true);

    try {
      // Upload file to Appwrite storage
      const result = await storage.createFile("670f7d00000fd9e6c594", "unique()", file); // Replace 'bucket_id' with your Appwrite bucket ID

      // Read file content using FileReader (for parsing text)
      const reader = new FileReader();
      reader.onload = async (event) => {
        const resumeText = event.target.result;

        // Call ChatGPT API via Appwrite endpoint
        const response = await fetch('http://670faa6966dc03bcb44c.appwrite.global/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: `Analyze this resume and provide job recommendations: ${resumeText}`
          }),
        });

        const data = await response.json();
        setAnalysisResult(data);
        setLoading(false);
      };
      reader.readAsText(file); // Reading resume content as text

      alert("File uploaded successfully!");
    } catch (error) {
      alert("Upload failed!");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen p-10 bg-gray-100">
      <div className="bg-white p-6 rounded shadow-md max-w-xl mx-auto">
        <h2 className="text-2xl font-bold mb-4">Welcome to Your Dashboard</h2>

        {/* Form to upload resume */}
        <form onSubmit={handleFileUpload}>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            type="submit"
            className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            disabled={loading}
          >
            {loading ? "Uploading..." : "Upload Resume"}
          </button>
        </form>

        {/* Display Analysis Results */}
        {analysisResult && (
          <div className="mt-6 p-4 bg-gray-100 rounded shadow">
            <h3 className="text-xl font-bold mb-4">Analysis Results</h3>
            <p><strong>Job Recommendations:</strong> {analysisResult.recommendations}</p>
            <p><strong>ATS Score:</strong> {analysisResult.ats_score}</p>
            <p><strong>Suggestions:</strong> {analysisResult.suggestions}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
